# Create SSoQE-compliant ggplot2 theme
theme_ssoqe <- function(base_size = text_size_base,
                       base_family = ssoqe_base_font,
                       base_line_size = line_size_base,
                       base_rect_size = line_size_base) {
  ggplot2::theme_bw(base_size = base_size,
                   base_family = base_family,
                   base_line_size = base_line_size,
                   base_rect_size = base_rect_size) +
  ggplot2::theme(
    # Text elements
    text = ggplot2::element_text(family = ssoqe_base_font, colour = "#1F2937"),
    plot.title = ggplot2::element_text(family = ssoqe_heading_font, colour = "#155560", face = "bold", size = ggplot2::rel(1.4)),
    plot.subtitle = ggplot2::element_text(family = ssoqe_base_font, colour = "#1F2937", size = ggplot2::rel(1.1)),
    axis.title = ggplot2::element_text(family = ssoqe_base_font, colour = "#1F2937", size = ggplot2::rel(1.0)),
    axis.text = ggplot2::element_text(family = ssoqe_base_font, colour = "#1F2937", size = ggplot2::rel(0.9)),
    legend.title = ggplot2::element_text(family = ssoqe_base_font, colour = "#1F2937", size = ggplot2::rel(1.0)),
    legend.text = ggplot2::element_text(family = ssoqe_base_font, colour = "#1F2937", size = ggplot2::rel(0.9)),
    strip.text = ggplot2::element_text(family = ssoqe_heading_font, colour = "#155560", face = "bold"),
    
    # Background elements
    plot.background = ggplot2::element_rect(fill = "#F2F4F2", colour = NA),
    panel.background = ggplot2::element_rect(fill = "#F2F4F2", colour = NA),
    legend.background = ggplot2::element_blank(),
    legend.key = ggplot2::element_blank()
  )
}

# Set the default ggplot2 theme to SSoQE theme
ggplot2::theme_set(theme_ssoqe())

# Helper functions for common SSoQE visualizations
ssoqe_discrete_colors <- function(n) {
  if (n <= length(ssoqe_cols)) {
    return(ssoqe_cols[1:n])
  } else {
    # Generate more colors using colorRampPalette if needed
    return(colorRampPalette(ssoqe_cols)(n))
  }
}

# Export commonly used values for backward compatibility
text_size <- text_size_base
line_size <- line_size_base
